import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Observable, catchError } from 'rxjs';
import { User } from '../models/user';


@Injectable({
  providedIn: 'root'
})
export class DataService {

  const httpOptions = {
    headers: new HttpHeaders({
      'Content-Type':  'application/json'
    })
  };

   // Inject ApiUrl in constructor to Get it form ather Service
   constructor(@Inject(String) private APIUrl: string,private http: HttpClient) { }


  // Get Method
  getAll(): Observable<any> {
    return this.http.get<any>(`${this.APIUrl}/`);
  }
  // Get with id
  get(id: any): Observable<any> {
    return this.http.get(`${this.APIUrl}/${id}`);
  }
  // Update Method
  Update(data: any): Observable<any> {
    return this.http.put(`${this.APIUrl}/update-user`, data);
  }
  // Create Method
  Create(user: User): Observable<User> {
    return this.http.post<User>(`${this.APIUrl}/register-user`, user, this.httpOptions);
  }
  // Delete Method
  Delete(id: any): Observable<any> {
    return this.http.delete(`${this.APIUrl}/delete/${id}`);
  }
}
