import { Component } from '@angular/core';
import { UserService } from '../../services/user.service';
import { User } from 'src/app/models/user';


@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss']
})
export class UserComponent {
  userList : User[] = [];
  // this variable is get data from model
  userToModify : User = new User();
  // this variable determines wither we are in changing or creating new user
  creatingMode : boolean = true;


  constructor(private userService : UserService){
    this.getAllUsers();
  }
  //Get All Users
  getAllUsers(){
    this.userService.getAll().subscribe((response : User[])=>{
      this.userList = response;
    });
  }

  //Update User
  modifyUser(){
    this.userService.Update(this.userToModify).subscribe(()=>{
      alert("User Updated Successfully");
      window.location.reload();
    })
  }
  //Create new User
  CreateUser(user: User): Observable{
      let newUser = {
        name : this.userToModify.name,
        surname : this.userToModify.surname,
        birthDate: this.userToModify.birthDate,
        email: this.userToModify.email,
        phoneNumber: this.userToModify.phoneNumber
      };
      this.userService.Create(newUser).subscribe(()=>{
        alert("User Added Successfully");
        window.location.reload();
      });
  }
  //Delete User
  deleteUser(userId : string){
    if(confirm("Are you sure you want to delete this user !!!")){
      this.userService.Delete(userId).subscribe(()=>{
        alert("User Deleted Successfully");
        window.location.reload();
      });
    }
  }
  // function to verify the event
  openModel(user : User = new User()){
    if(user.id == ""){
      this.userToModify = new User();
    }else{
      this.creatingMode = false
      this.userToModify = user;
    }
  }
}
<button type="button" class="btn btn-success" data-toggle="modal" data-target="#EditAddUser" (click)="openModel()" style="margin: 50px;">+ Novo Usuário</button>
<table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Nome</th>
      <th scope="col">Sobrenome</th>
      <th scope="col">Data de Nascimento</th>
      <th scope="col">Email</th>
      <th scope="col">Número de telefone</th>
      <th scope="col" colspan="2">Ação</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of userList" >
      <th scope="row">{{user.id}}</th>
      <td>{{user.name}}</td>
      <td>{{user.surname}}</td>
      <td>{{user.birthDate}}</td>
      <td>{{user.email}}</td>
      <td>{{user.phoneNumber}}</td>
      <td>
        <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#EditAddUser" (click)="openModel(user)">Edit</button>
      </td>
      <td>
        <button type="button" class="btn btn-danger" (click)="deleteUser(user.id)">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
<!-- Modal -->
<div class="modal fade" id="EditAddUser" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Adicionar Usuário</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <div class="input-group">
            <form>
                <br>
                <label for="fname">Nome</label><br>
                <input type="text" class="form-control" [(ngModel)]="userToModify.name">
                <label for="sname">Sobrenome</label><br>
                <input type="text" class="form-control" [(ngModel)]="userToModify.surname">
                <label for="bdate">Data de Nascimento</label><br>
                <input type="text" class="form-control" [(ngModel)]="userToModify.birthDate">
                <label for="email">Email</label><br>
                <input type="text" class="form-control" [(ngModel)]="userToModify.email">
                <label for="phone">Número de Telefone</label><br>
                <input type="text" class="form-control" [(ngModel)]="userToModify.phoneNumber">
            </form>
        </div><br>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary" *ngIf="creatingMode" (click)="CreateUser()">Create User</button>
        <button type="button" class="btn btn-primary" *ngIf="!creatingMode" (click)="modifyUser()">Modify User</button>
      </div>
    </div>
  </div>
</div>
